import React from "react";
import Logo from "./Logo";
import Sidebar from "./SideBar";
import { useCart } from "./CartContext";

const Payment = () => {
  let { count, setCount } = useCart();

  const mycount = () => {
    count = count + 1;
    setCount(count);
    return count;
  };

  const getdeliveryDate = () => {
    // Get today's date
    const today = new Date();

    const futureDate = new Date();
    futureDate.setDate(today.getDate() + 18);

    // Format the date if needed
    const formattedDate = `${futureDate.getFullYear()}-${(
      futureDate.getMonth() + 1
    )
      .toString()
      .padStart(2, "0")}-${futureDate.getDate().toString().padStart(2, "0")}`;

    console.log(formattedDate); // Output: YYYY-MM-DD
  };
  return (
    <div>
      <Logo />
      <Sidebar />
      <div>
        <h2>Order</h2>
        <h3>Your Order is stored</h3>
        <h3>Your Order number is::{mycount()}</h3>
        <h3>Your Order Will be delivered on::</h3>
      </div>
    </div>
  );
};

export default Payment;
