import axios from "axios";
import Sidebar from "./SideBar";
import Logo from "./Logo";
import { useLocation } from "react-router-dom";
import { React, useState, useEffect } from "react";

const textStyle = {
  width: "100px",
  borderRight: "1px solid rgb(221, 221, 221)",
  paddingRight: "10px",
  borderBottom: "1px solid rgb(221, 221, 221)",
};

const ViewReview = (e) => {
  const [views, setViews] = useState([]);
  const [product, setProduct] = useState();
  const location = useLocation();

  useEffect(() => {
    setProduct(location.state || []);
    console.log("product", product);
    const fetchData = async () => {
      try {
        await axios
          .get("http://localhost:9000/api/ViewReview", product)
          .then((res) => console.log(res.data))
          .catch((err) => console.log(err))
          .finally(() => console.log("done"));
      } catch (error) {
        console.error("Error:", error);
      }
    };
    fetchData();
  }, []);

  return (
    <div>
      <Logo />
      <Sidebar />
      <div>
        <h2
          style={{
            borderBottom: "3px solid black",
            fontSize: "24px",
            color: "purple",
            textAlign: "center",
          }}
        >
          Reviews
        </h2>
        {views.length > 0 ? (
          <div style={{ marginLeft: "10px" }}>
            <table
              style={{
                border: "1px solid rgb(221, 221, 221)",
                borderCollapse: "collapse",
                padding: "10px",
                marginLeft: "220px",
              }}
            >
              <thead>
                <tr>
                  <th style={textStyle}>Product Model Name:</th>
                  <th style={textStyle}>Product Price:</th>
                </tr>
              </thead>
              <tbody>
                {views.map((item, index) => (
                  <tr key={index}>
                    <td style={textStyle}>{item.productName}</td>
                    <td style={textStyle}>${item.productPrice}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <p style={{ textAlign: "center", color: "brown", fontSize: "20px" }}>
            There are No Reviews for this Product.
          </p>
        )}
      </div>

      {/* {cartItems.length > 0 && <Carousel items={carouselItems} />} */}
    </div>
  );
};

export default ViewReview;
