import React, { useState } from "react";
import { useData } from "./Data";

const img = {
  WebkitTextSizeAdjust: "100%",
  WebkitTapHighlightColor: "rgba(0,0,0,0)",
  color: "#333",
  fontFamily: 'Georgia, "Times New Roman", Times, serif',
  fontSize: "12px !important",
  borderSpacing: 0,
  borderCollapse: "collapse",
  lineHeight: "240%",
  listStyle: "none",
  boxSizing: "unset",
  border: 0,
  verticalAlign: "middle",
  display: "block",
  width: "180px",
  height: "160px",
  marginLeft: "auto",
  marginRight: "auto",
};

const Carousel = ({ items }) => {
  const { accessories } = useData();

  const [activeIndex, setActiveIndex] = useState(0);

  const allRelatedItemsArray = items?.flatMap((item) =>accessories?.filter((element) => item.accessories.includes(element.id)));

  const handlePrev = () => {
    setActiveIndex((prevIndex) =>
      prevIndex === 0 ? items.length - 1 : prevIndex - 1
    );
  };

  const handleNext = () => {
    setActiveIndex((prevIndex) =>
      prevIndex === items.length - 1 ? 0 : prevIndex + 1
    );
  };

  return (
    <div>
      <h2>Checkout Carousel</h2>
      <div className="carousel">
        <button onClick={handlePrev}>Previous</button>
        {
          <div style={{display:'flex',fle:'column'}}>

            {allRelatedItemsArray.map((item, index) => {
              return (
                <div style={{display:'flex',flexDirection:'column'}}>
                  <h3>{item.name}</h3>
                  <p>{item.price}</p>
                  <img style={img} src={`images/${item.type}/${item.image}`} alt={item.name} />
                </div>
              );
            })}
          </div>
        }
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};
export default Carousel;
