import React from "react";
import Logo from "./Logo";
import Sidebar from "./SideBar";
import { useState } from "react";
import { useData } from "./Data"; // instead of importing each of the products like this import { doorBells, doorLocks, lightings, speakers, thermostats } from "./Data"; we can import all of them in one go like this import { useData } from "./Data";
import { DataProvider } from "./Data";

const ProductForm = () => {
  const [productId, setProductId] = useState("");
  const [type, setType] = useState("");
  const [productName, setProductName] = useState("");
  const [productPrice, setProductPrice] = useState("");
  const [productImage, setProductImage] = useState("");
  const [productManufacturer, setProductManufacturer] = useState("");
  const [productCondition, setProductCondition] = useState("");
  const [productDiscount, setProductDiscount] = useState("");
  const [product, setProduct] = useState("");
  const formStyle = {
    margin: "20px",
  };

  const inputStyle = {
    width: "100%",
    padding: "8px",
    margin: "5px 0",
    boxSizing: "border-box",
  };

  const buttonStyle = {
    float: "right",
    height: "20px",
    margin: "20px",
    marginRight: "10px",
  };

  const { doorBells, addProduct, doorLocks, lightings, speakers, thermostats } =
    useData(); //here we can access all the products from the useData hook

  const handleForm = (e) => {
    e.preventDefault();
    const productData = {
      id: "productId",
      name: "productName",
      price: "productPrice",
      image: "productImage",
      manufacturer: "productManufacturer",
      condition: "productCondition",
      discount: "productDiscount",
      type: "type",
      // id: productId,
      // name: productName,
      // price: productPrice,
      // image: productImage,
      // manufacturer: productManufacturer,
      // condition: productCondition,
      // discount: productDiscount,
      // type: type,
    };
    addProduct(productData);
    console.log(productData);
    console.log(doorBells);
  };

  return (
    <div>
      <Logo />
      <Sidebar />
      <div className="entry">
        <form onSubmit style={formStyle}>
          <table id="bestseller">
            <tbody>
              <tr>
                <td>
                  <h3>Product Type</h3>
                </td>
                <td>
                  <select
                    onChange={(e) => setType(e.target.value)}
                    name="producttype"
                  >
                    <option value="default" selected>
                      ---
                    </option>
                    <option value="doorBell">Door Bells</option>
                    <option value="doorLock">Door Locks</option>
                    <option value="lighting">Lightings</option>
                    <option value="speaker">Speakers</option>
                    <option value="Thermostat">Thermostats</option>
                    <option value="accessories">Accessory</option>
                  </select>
                </td>
              </tr>
              <tr>
                <td>
                  <h3>Product</h3>
                </td>
                <td>
                  <input
                    type="text"
                    name="product"
                    placeholder="Please mention product if adding accessories"
                    value={product}
                    className="input"
                    style={inputStyle}
                    onChange={(e) => setProduct(e.target.value)}
                  />
                </td>
              </tr>
              <tr>
                <td>
                  <h3>Product Id</h3>
                </td>
                <td>
                  <input
                    type="text"
                    name="productId"
                    value={productId}
                    className="input"
                    //required
                    style={inputStyle}
                    onChange={(e) => setProductId(e.target.value)}
                  />
                </td>
              </tr>
              <tr>
                <td>
                  <h3>Product Name</h3>
                </td>
                <td>
                  <input
                    type="text"
                    name="productName"
                    value={productName}
                    className="input"
                    //required
                    style={inputStyle}
                    onChange={(e) => setProductName(e.target.value)}
                  />
                </td>
              </tr>
              <tr>
                <td>
                  <h3>Product Price</h3>
                </td>
                <td>
                  <input
                    type="number"
                    step="any"
                    placeholder="please enter numeric data"
                    name="productPrice"
                    value={productPrice}
                    className="input"
                    //required
                    style={inputStyle}
                    onChange={(e) => setProductPrice(e.target.value)}
                  />
                </td>
              </tr>
              <tr>
                <td>
                  <h3>Product Image</h3>
                </td>
                <td>
                  <input
                    type="text"
                    name="productImage"
                    value={productImage}
                    className="input"
                    style={inputStyle}
                    onChange={(e) => setProductImage(e.target.value)}
                  />
                </td>
              </tr>
              <tr>
                <td>
                  <h3>Product Manufacturer</h3>
                </td>
                <td>
                  <input
                    type="text"
                    name="productManufacturer"
                    value={productManufacturer}
                    className="input"
                    //required
                    style={inputStyle}
                    onChange={(e) => setProductManufacturer(e.target.value)}
                  />
                </td>
              </tr>
              <tr>
                <td>
                  <h3>Product Condition</h3>
                </td>
                <td>
                  <input
                    type="text"
                    name="productCondition"
                    value={productCondition}
                    className="input"
                    //required
                    style={inputStyle}
                    onChange={(e) => setProductCondition(e.target.value)}
                  />
                </td>
              </tr>
              <tr>
                <td>
                  <h3>Product Discount</h3>
                </td>
                <td>
                  <input
                    type="number"
                    step="any"
                    placeholder="please enter numeric data"
                    name="productDiscount"
                    value={productDiscount}
                    className="input"
                    //required
                    style={inputStyle}
                    onChange={(e) => setProductDiscount(e.target.value)}
                  />
                </td>
              </tr>
              <tr>
                <td>
                  <input
                    type="submit"
                    className="btnbuy"
                    name="button"
                    value="add"
                    style={buttonStyle}

                  />
                </td>
              </tr>
              <tr>
                <td></td>
                <td></td>
              </tr>
            </tbody>
          </table>
        </form>
      </div>
    </div>
  );
};

export default ProductForm;
