import React, { useState } from "react";
import { useData } from "./Data";

const itemStyle = {
  WebkitTextSizeAdjust: "100%",
  WebkitTapHighlightColor: "rgba(0,0,0,0)",
  color: "#333",
  fontFamily: 'Georgia, "Times New Roman", Times, serif',
  fontSize: "12px !important",
  borderSpacing: 0,
  borderCollapse: "collapse",
  lineHeight: "240%",
  listStyle: "none",
  boxSizing: "unset",
  marginBottom: "5px",
  marginTop: "6px",
  border: "1px solid #000",
};

const shopitem = {
  WebkitTextSizeAdjust: "100%",
  WebkitTapHighlightColor: "rgba(0,0,0,0)",
  lineHeight: "1.42857143",
  color: "#333",
  fontFamily: 'Georgia, "Times New Roman", Times, serif',
  fontSize: "12px !important",
  borderSpacing: 0,
  borderCollapse: "collapse",
  boxSizing: "unset",
  borderRadius: "5px",
  border: "1px solid #000",
  padding: "4px",
  width: "200px",
  marginLeft: "auto",
  marginRight: "auto",
};

const img = {
  WebkitTextSizeAdjust: "100%",
  WebkitTapHighlightColor: "rgba(0,0,0,0)",
  color: "#333",
  fontFamily: 'Georgia, "Times New Roman", Times, serif',
  fontSize: "12px !important",
  borderSpacing: 0,
  borderCollapse: "collapse",
  lineHeight: "240%",
  listStyle: "none",
  boxSizing: "unset",
  border: 0,
  verticalAlign: "middle",
  display: "block",
  width: "180px",
  height: "160px",
  marginLeft: "auto",
  marginRight: "auto",
};

const Carousel = ({ items }) => {
  const { accessories } = useData();

  const [activeIndex, setActiveIndex] = useState(0);

  const allRelatedItemsArray = items?.flatMap((item) =>accessories?.filter((element) => item.accessories.includes(element.id)));

  const handleBuyNow = (doorBell) => {
    if (!loggedUser) {
      navigate("/Login",{state:{from:'Please Login to add items to cart'}});
      return;
    }
    addToCart(doorBell);
    navigate("/Cart");
  };

  const handlePrev = () => {
    setActiveIndex((prevIndex) =>
      prevIndex === 0 ? items.length - 1 : prevIndex - 1
    );
  };

  const handleNext = () => {
    setActiveIndex((prevIndex) =>
      prevIndex === items.length - 1 ? 0 : prevIndex + 1
    );
  };

  return (
    <div>
      <h2>Checkout Carousel</h2>
      <div style={{border: "1px solid #ddd"}} >
        <button onClick={handlePrev}>Previous</button>
        {
          <div style={{display:'flex',flexGrow:'initial', padding:'10px'}}>

            {allRelatedItemsArray.map((item, index) => {
              return (

                <ul style={{marginLeft:'10px',display:'flex',flexDirection:'column',border: "1px solid #ddd"}}>
                  <li style={item}>
                    <img style={img} src={`images/${item.type}/${item.image}`} alt="" />
                  </li>
                  <button
                    style={shopitem}
                    type="button"
                    onClick={() => handleBuyNow(item)}
                  >
                    Buy Now
                  </button>
                </ul>
              );
            })}
          </div>
        }
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};
export default Carousel;
