import React, { useState,useEffect } from "react";

const Carousel = ({ items }) => {
  const [array, setArray] = useState([]);
  const [activeIndex, setActiveIndex] = useState(0);
  const [carouselItems, setCarouselItems] = useState([]); // State for items to be displayed in Carousel


  const allRelatedItemsFunc = () => items.flatMap((item) => item.accessories);
  const allRelatedItemsArray=allRelatedItemsFunc();


  useEffect(() => {
    // Update carouselItems whenever cartItems changes
    setCarouselItems(allRelatedItemsArray);
  }, [allRelatedItemsArray]);

  const handlePrev = () => {
    setActiveIndex((prevIndex) =>
      prevIndex === 0 ? items.length - 1 : prevIndex - 1
    );
  };

  const handleNext = () => {
    setActiveIndex((prevIndex) =>
      prevIndex === items.length - 1 ? 0 : prevIndex + 1
    );
  };

  return (
    <div>
      <h2>Checkout Carousel</h2>
      <div className="carousel">
        <button onClick={handlePrev}>Previous</button>
        { 
          <div className="carousel-content">
            {console.log(allRelatedItemsArray)}
            {allRelatedItemsArray.map((item, index) => (
              <div
                key={index}
                className={index === activeIndex ? "active" : "inactive"}
              >
                <h3>{item.name}</h3>
                <p>{item.description}</p>
                {/* Add more details as needed */}
              </div>
            ))}
          </div>
        }
        <button onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};
export default Carousel;
