import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import Logo from "./Logo";
import SideBar from "./SideBar";
import { useUser } from "./UserContext";
import { useCart } from "./CartContext";
import {useData} from "./Data";



const img = {
  WebkitTextSizeAdjust: "100%",
  WebkitTapHighlightColor: "rgba(0,0,0,0)",
  color: "#333",
  fontFamily: 'Georgia, "Times New Roman", Times, serif',
  fontSize: "12px !important",
  borderSpacing: 0,
  borderCollapse: "collapse",
  lineHeight: "240%",
  listStyle: "none",
  boxSizing: "unset",
  border: 0,
  verticalAlign: "middle",
  display: "block",
  width: "180px",
  height: "160px",
  marginLeft: "auto",
  marginRight: "auto",
};

const item = {
  WebkitTextSizeAdjust: "100%",
  WebkitTapHighlightColor: "rgba(0,0,0,0)",
  color: "#333",
  fontFamily: 'Georgia, "Times New Roman", Times, serif',
  fontSize: "12px !important",
  borderSpacing: 0,
  borderCollapse: "collapse",
  lineHeight: "240%",
  listStyle: "none",
  boxSizing: "unset",
  marginBottom: "5px",
  marginTop: "6px",
  border: "1px solid #000",
};

const shopitem = {
  WebkitTextSizeAdjust: "100%",
  WebkitTapHighlightColor: "rgba(0,0,0,0)",
  lineHeight: "1.42857143",
  color: "#333",
  fontFamily: 'Georgia, "Times New Roman", Times, serif',
  fontSize: "12px !important",
  borderSpacing: 0,
  borderCollapse: "collapse",
  boxSizing: "unset",
  borderRadius: "5px",
  border: "1px solid #000",
  padding: "4px",
  width: "200px",
  marginLeft: "auto",
  marginRight: "auto",
};
const DoorBell = (e) => {
  const { loggedUser } = useUser();
  const { addToCart } = useCart();
  const {doorBells} =useData()
  const navigate = useNavigate();
  const location = useLocation();

  let doorBellsData = doorBells;


  // the request from the sidebar is sent as a query parameter and is accessed using the URLSearchParams API. The to parameter of the link can hold the query parameter.
  const makerFilter = new URLSearchParams(location.search).get("maker");

  const handleBuyNow = (doorBell) => {
    if (!loggedUser) {
      navigate("/Login",{state:{from:'Please Login to add items to cart'}});
      return;
    }
    addToCart(doorBell);
    navigate("/Cart");
  };

  const filteredDoorBellsData =
    makerFilter == null
      ? doorBellsData
      : doorBellsData.filter((doorBell) => doorBell.manufacturer === makerFilter);

  return (
    <div>
      <Logo />
      <SideBar />
      <div id="page">
        <h2>
          <p style={{ fontSize: "24px" }}>Door Bells</p>
        </h2>
        <table>
          <tbody>
            {filteredDoorBellsData.map((doorBell, index) => (
              <tr key={index}>
                <td>
                  <div>
                    <h3>{doorBell.name}</h3>
                    <strong>${doorBell.price}</strong>
                    <ul>
                      <li style={item}>
                        <img style={img} src={doorBell.image} alt="" />
                      </li>
                      <button
                        style={shopitem}
                        type="button"
                        onClick={() => handleBuyNow(doorBell)}
                      >
                        Buy Now
                      </button>
                    </ul>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default DoorBell;
