import React from "react";
import Logo from "./Logo";
import Sidebar from "./SideBar";
import { useCart } from "./CartContext";


const textStyle=()=>{
  return{
    color:"red"
  }
}
const smallTextSizeStyle = {
  color: "blue",
  fontSize: "12px",
};

const Payment = () => {
  let { count } = useCart();

  const getdeliveryDate = () => {
    const futureDate = new Date();
    futureDate.setDate(new Date().getDate() + 18);

    // Format the date if needed
    const formattedDate = `${futureDate.getFullYear()}-${(
      futureDate.getMonth() + 1
    )
      .toString()
      .padStart(2, "0")}-${futureDate.getDate().toString().padStart(2, "0")}`;

    return formattedDate; // Output: YYYY-MM-DD
  };
  return (
    <div>
      <Logo />
      <Sidebar />
      <div>
        <h2>Order</h2>
        <h3>Your Order is stored</h3>
        <h3>Your Order number is::{count}</h3>
        <h3>Your Order Will be delivered on::{getdeliveryDate()}</h3>
      </div>
    </div>
  );
};

export default Payment;
