import React from "react";
import Logo from "./Logo";
import Sidebar from "./SideBar";
import { useState } from "react";
import { useData } from "./Data"; // instead of importing each of the products like this import { doorBells, doorLocks, lightings, speakers, thermostats } from "./Data"; we can import all of them in one go like this import { useData } from "./Data";

const ProductDelete = () => {
  const [productId, setProductId] = useState("");
  const [submitSuccess, setSubmitSuccess] = useState(false);
  const formStyle = {
    margin: "20px",
  };

  const inputStyle = {
    width: "100%",
    padding: "8px",
    margin: "5px 0",
    boxSizing: "border-box",
  };

  const buttonStyle = {
    float: "right",
    height: "20px",
    margin: "20px",
    marginRight: "10px",
  };

  const { deleteProduct } = useData(); //here we can access all the products from the useData hook

  const handleForm = (e) => {
    e.preventDefault();
    con
    deleteProduct(productId);
    setSubmitSuccess(true);
  };

  return (
    <div>
      <Logo />
      <Sidebar />
      <div className="entry">
        <h2 style={{ textAlign: "center" }}>Update Product</h2>
        <form onSubmit={handleForm} style={formStyle}>
          <table id="bestseller">
            <tbody>
              <tr>
                <td>
                  <h3>Product Id</h3>
                </td>
                <td>
                  <input
                    type="text"
                    name="productId"
                    value={productId}
                    className="input"
                    required
                    style={inputStyle}
                    onChange={(e) => setProductId(e.target.value)}
                  />
                </td>
              </tr>

              <tr>
                <td>
                  <input
                    type="submit"
                    className="btnbuy"
                    name="button"
                    value="delete"
                    style={buttonStyle}
                  />
                </td>
              </tr>
              <tr>
                <td></td>
                <td></td>
              </tr>
            </tbody>
          </table>
        </form>
        {submitSuccess && (
          <p style={{ color: "green", textAlign: "center" }}>
            Product Deleted Successfully
          </p>
        )}
      </div>
    </div>
  );
};

export default ProductDelete;
