  borderCollapse: "collapse",
  lineHeight: "240%",
  listStyle: "none",
  boxSizing: "unset",
  border: 0,
  verticalAlign: "middle",
  display: "block",
  width: "180px",
  height: "160px",
  marginLeft: "auto",
  marginRight: "auto",
};

const Carousel = ({ items }) => {
  const { loggedUser } = useUser();
  const { addToCart } = useCart();
  const { navigate } = useNavigate();
