      });
      return;
    }
    addToCart(doorBell);
    navigate("/Cart");
  };

  return (
    <div>
      <h2>Accessories</h2>
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          margin: "10px 60px 10px 230px",
          border: "1px solid #ddd",
