import React from "react";
import Logo from "./Logo";
import Sidebar from "./SideBar";
import { useUser } from "./UserContext";

const ViewOrder = () => {
  const { loggedUser } = useUser();
  const orderList =
    JSON.parse(localStorage.getItem(`order_${loggedUser.username}`)) || [];


  return (
    <div>
      <Logo />
      <Sidebar />
      <h2>Order</h2>
      {orderList.map((item, index) => {
        console.log("my new item is ", item);
        return (
          <div key={index}>
            <h3
              style={{ color: "purple", fontSize: "20px", fontWeight: "bold" }}
            >
              Your Order {index + 1} due on {item[0].deliveryDate}
            </h3>
            <table>
              <thead>
                <tr>
                  <th>Item</th>
                  <th>Price</th>
                </tr>
              </thead>
              <tbody>
                {item.map((item, index) => {
                  return (
                    <tr key={index}>
                      <td>{item.name}</td>
                      <td>${item.price}</td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        );
      })}
    </div>
  );
};

export default ViewOrder;
