// UserContext.js
import React, { createContext, useContext, useState } from 'react';

const UserContext = createContext();
const [Uid, setUid] = useState("");

export const UserProvider = ({ children }) => {
  const [loggedUser, setLoggedUser] = useState({username:"q",uId:6,password:"q",usertype:"retailer"});
//{username:"q",password:"q",usertype:"retailer"}
  const login = (username, password,usertype) => {
    setLoggedUser({ username,uId, password ,usertype});
  };

  const logout = () => {
    setLoggedUser(null);
  };

  return (
    <UserContext.Provider value={{loggedUser,Uid,setUid, login, logout }}>
      {children}
    </UserContext.Provider>
  );
};

export const useUser = () => {
  const context = useContext(UserContext);

  if (!context) {
    throw new Error('useUser must be used within a UserProvider');
  }
  return context;
};
