import React from "react";
import Logo from "./Logo";
import Sidebar from "./SideBar";
import { useCart } from "./CartContext";

const Payment = () => {
  let { count, setCount } = useCart();
  const mycount =()=>{
    count=count+1;
    setCount(count);
    return count;
  }
  return (
    <div>
      <Logo />
      <Sidebar />
      <div>
        <h2>Order</h2>
        <h3>Your Order  is stored</h3>
        <h3>Your Order  number is::{mycount()}</h3>
        <h3>Your Order  is stored</h3>
      </div>
    </div>
  );
};

export default Payment;
