  const { accessories } = useData();

  const [activeIndex, setActiveIndex] = useState(0);

  const allRelatedItemsArray = items.flatMap((item) => {
    return accessories.map((element) => {
      console.log("element.id, accessories:::", element.id, item.accessories);
      if( item.accessories.includes(element.id)){
        return element
      }
    });
  });

