import React from "react";
import Logo from "./Logo";
import Sidebar from "./SideBar";
import { useUser } from "./UserContext";


const ViewOrder = () => {
  const { loggedUser } = useUser();
  const orderList =
    JSON.parse(localStorage.getItem(`order_${loggedUser.username}`)) || [];
  return (
    <div>
      <Logo />
      <Sidebar />
      <h2>Order</h2>
      {console.log('OrderList is ',orderList)}
      {orderList.flatMap((item, index) => {
       console.log(item)
        return (
          <div key={index}>

            <h3 style={{ color: "purple", fontSize: "20px", fontWeight: "bold" }}>
              Your Order {index+1} due on {item.deliveryDate}
            </h3>
            <p >
              Your Order Will be delivered on::{item.deliveryDate}
            </p>
          </div>
        );
      })}
    </div>
  );
};

export default ViewOrder;
